typedef enum {
	GNOME_ICON_LIST_ICONS,
	GNOME_ICON_LIST_TEXT_BELOW,
	GNOME_ICON_LIST_TEXT_RIGHT
} GnomeIconListMode;

GtkWidget*  gnome_icon_list_new             (guint icon_width,
                                             GtkAdjustment *adj,
                                             int flags);

